import React, { useState } from "react";

import ExpenseItem from "../ExpenseItem/ExpenseItem";
import "./Expenses.css";
import Card from "../../UI/Card/Card";
import ExpensesFilter from "../ExpenseFilter/ExpensesFilter";

const Expenses = (props) => {
  // Create years array from starting year to current year. [startingYear, ..., currentYear]
  // I.e. if startingYear is 2019, and currentYear is 2021, years will be [2019, 2020, 2021]
  const startingYear = 2019;
  const numYears = new Date().getFullYear() - startingYear + 1;
  const years = Array.from(
    { length: numYears },
    (_, index) => startingYear + index
  );

  const [filteredYear, setFilteredYear] = useState(startingYear);

  const setFilteredYearWrapper = (year) => {
    setFilteredYear(year);
  };

  const filteredExpenses = props.expenses.filter(
    (expense) => Number(filteredYear) === new Date(expense.date).getFullYear()
  );

  return (
    <Card className="expenses">
      <ExpensesFilter
        value={filteredYear}
        onSelectYear={setFilteredYearWrapper}
      />
      {filteredExpenses.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </Card>
  );
};

export default Expenses;
