import React, { useState } from "react";

import ExpenseItem from "../ExpenseItem/ExpenseItem";
import "./Expenses.css";
import Card from "../../UI/Card/Card";
import exps, { addExpense } from "../../../data/expenses";

const Expenses = (props) => {
  const [expenses, setExpenses] = useState(exps);

  const addExp = (expense) => {
    addExpense(expense);

    console.log(`Expenses from import: ${exps}`);
    console.log(`Expenses from state: ${expenses}`);
  };

  return (
    <Card className="expenses" addExpense={addExp}>
      {expenses.map((expense) => (
        <ExpenseItem
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </Card>
  );
};

export default Expenses;
