import React, { useState } from "react";

import ExpenseItem from "../ExpenseItem/ExpenseItem";
import "./Expenses.css";
import Card from "../../UI/Card/Card";
import ExpensesFilter from "../ExpenseFilter/ExpensesFilter";

const Expenses = (props) => {
  const { expenses } = props;
  const filterExpensesHandler = props.onFilter;

  const filterByYearHandler = (year) => {
    const filteredExpenses = expenses.filter(
      (expense) => Number(year) === new Date(expense.date).getFullYear()
    );

    filterExpensesHandler(filteredExpenses);
  };

  return (
    <Card className="expenses">
      <ExpensesFilter onSelectYear={filterByYearHandler} />
      {expenses.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </Card>
  );
};

export default Expenses;
