import React, { useState } from "react";

import ExpenseItem from "../ExpenseItem/ExpenseItem";
import "./Expenses.css";
import Card from "../../UI/Card/Card";
import ExpensesFilter from "../ExpenseFilter/ExpensesFilter";
import initialExpenseData from "../../../data/expenses";

const Expenses = (props) => {
  // const { expenses } = props;
  const [expenses, setExpenses] = useState(initialExpenseData);

  const addExpenseHandler = (newExpense) => {
    setExpenses((prevExpenses) => [newExpense, ...prevExpenses]);
  };

  const filterByYearHandler = (year) => {
    const filteredExpenses = expenses.filter(
      (expense) => Number(year) === new Date(expense.date).getFullYear()
    );

    console.log(filteredExpenses);
  };

  return (
    <Card className="expenses">
      <ExpensesFilter onSelectYear={filterByYearHandler} />
      {expenses.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </Card>
  );
};

export default Expenses;
